import React from "react";
import { useAuth } from "../context/AuthContext";
import { signOut } from "firebase/auth";
import { useNavigate } from "react-router-dom";
import UserProfile from "./userprofile.widget";
import UserList from "./userList";
import WeatherWidget from "./weather.widget";
import Stocks from "./stock.widget";
import Quotes from "./quotes.widget";
import Calculator from "./Calculator";
import Checklist from "./Checklist";
import Sidebar from "./sidebar.component";
import { auth } from "../firebase/firebase"; // Import the Firebase auth object
import "../styles/dashboard.css";

const Dashboard = () => {
    const { currentUser } = useAuth();
    const navigate = useNavigate(); // Used for redirecting the user

    // Function to handle logout
    const handleLogout = async () => {
        try {
            await signOut(auth); // Firebase sign out
            navigate("/"); // Redirect to login page after logout
        } catch (error) {
            console.error("Error signing out: ", error);
        }
    };

    return (
        <div className="dashboard">
            <Sidebar />
            <div className="widgets">
                <h1>Dashboard</h1>
                <button className="logout-button" onClick={handleLogout}>Logout</button> {/* Add Logout button */}
                <UserProfile />
                <UserList />
                <WeatherWidget />
                <Stocks />
                <Quotes />
                <Calculator />
                <Checklist />
            </div>
        </div>
    );
};

export default Dashboard;
