import React, { useState } from "react";
import { useAuth } from "../context/AuthContext";
import { signOut } from "firebase/auth";
import { useNavigate } from "react-router-dom";
import Sidebar from "./sidebar.component"; 
import UserProfile from "./userprofile.widget";
import UserList from "./userList";
import WeatherWidget from "./weather.widget";
import Quotes from "./quotes.widget";
import Calculator from "./Calculator";
import Checklist from "./Checklist";
import { auth } from "../firebase/firebase";
import StocksNews from "./stock.widget";
import "../styles/dashboard.css";

const Dashboard = () => {
    const { currentUser } = useAuth();
    const navigate = useNavigate();
    const [sidebarVisible, setSidebarVisible] = useState(false); // Sidebar visibility state

    const handleLogout = async () => {
        try {
            await signOut(auth);
            navigate("/");
        } catch (error) {
            console.error("Error signing out: ", error);
        }
    };

    // Toggle sidebar visibility
    const toggleSidebar = () => {
        setSidebarVisible(!sidebarVisible);
    };

    return (
        <div className="dashboard">
            {/* Toggle button visible only on mobile */}
            <button className="sidebar-toggle" onClick={toggleSidebar}>
                {sidebarVisible ? "Close Sidebar" : "Open Sidebar"}
            </button>

            {/* Sidebar visibility based on state */}
            <div className={sidebarVisible ? "sidebar visible" : "sidebar hidden"}>
                <Sidebar handleLogout={handleLogout} />
            </div>

            <h1 className="dashboard-title">Dashboard</h1>

            <div className="widgets">
                <UserProfile />
                <UserList />
                <WeatherWidget />
                <Quotes />
                <Calculator className="bottom-widget" />
                <Checklist className="bottom-widget" />
            </div>
            <div className="full-width-widget-container">
                <Stocks className="full-width-widget" />
            </div>
        </div>
    );
};

export default Dashboard;
