import React, { useState } from "react";
import { useAuth } from "../context/AuthContext";
import { signOut } from "firebase/auth";
import { useNavigate } from "react-router-dom";
import Sidebar from "./sidebar.component"; 
import UserProfile from "./userprofile.widget";
import UserList from "./userList";
import WeatherWidget from "./weather.widget";
import Quotes from "./quotes.widget";
import Calculator from "./Calculator";
import Checklist from "./Checklist";
import StocksNews from "./stock.widget";
import { auth } from "../firebase/firebase";
import "../styles/dashboard.css";

const Dashboard = () => {
    const { currentUser } = useAuth();
    const navigate = useNavigate();
    const [sidebarVisible, setSidebarVisible] = useState(false); // State to track sidebar visibility

    const handleLogout = async () => {
        try {
            await signOut(auth);
            navigate("/");
        } catch (error) {
            console.error("Error signing out: ", error);
        }
    };

    const toggleSidebar = () => {
        setSidebarVisible(!sidebarVisible); // Toggle sidebar visibility
    };

    return (
        <div className="dashboard">
            <button className="sidebar-toggle" onClick={toggleSidebar}>
                &#9776; {/* Three-bar icon */}
            </button>
            <h1 className="dashboard-title">Dashboard</h1>
            <Sidebar handleLogout={handleLogout} className={sidebarVisible ? "visible" : "hidden"} />
            <div className="widgets">
                <UserProfile />
                <UserList />
                <WeatherWidget />
                <Quotes />
                <Calculator className="bottom-widget" />
                <Checklist className="bottom-widget" />
            </div>
            <div className="full-width-widget-container">
                <StocksNews className="full-width-widget" />
            </div>
        </div>
    );
};

export default Dashboard;
